  apiRequestQuestions() {
    var API_URL = config.API_LINK;
    var API_PARAM = config.API_PARAMETERS;
    var API_KIOSID = config.API_KIOSKID;
    var JOBID = this.state.chosenJobId;
    var formData = new FormData();
    formData.append('kioskid', API_KIOSID);
    formData.append('jobid', JOBID);
    formData.append('appid', this.state.apiAppId);
    fetch(API_URL + 'api/kiosk/job-app/ques-list' + API_PARAM, {
      method: 'POST',
      body: formData
    }).then(response => {
      return response.json();
    }).then(json => {
      console.log("Result Questions:", json);

      if (json.code === "00") {
        this.saveLocalQues(json);
        this.getLocalQuestions();
      } else {
        alert(json.message[0]);
      }
    }).catch(error => {
      alert('System error');
      console.log('There has been a problem with fetching (Questions API): ', error);
    });
  }

  apiSaveQuestions() {
    var ANSWERS = this.state.apiQues;
    var finalAnswer = {};
    ANSWERS.forEach(function (qst) {
      var quesid = qst.quesid;
      var eachAns = {};
      eachAns["ansid"] = qst.ansid;
      eachAns["anstext"] = qst.anstext;
      finalAnswer[quesid] = eachAns;
    });
    finalAnswer = JSON.stringify(finalAnswer); //

    var API_URL = config.API_LINK;
    var API_PARAM = config.API_PARAMETERS;
    var API_KIOSID = config.API_KIOSKID;
    var JOBID = this.state.chosenJobId;
    var formData = new FormData();
    formData.append('kioskid', API_KIOSID);
    formData.append('jobid', JOBID);
    formData.append('appid', this.state.apiAppId);
    formData.append('ans', finalAnswer);
    fetch(API_URL + 'api/kiosk/job-app/ques-submit' + API_PARAM, {
      method: 'POST',
      body: formData
    }).then(response => {
      return response.json();
    }).then(json => {
      console.log("Result Saving Questions:", json);

      if (json.code === "00") {
        this.apiGetLocation();
      } else {
        alert(json.message[0]);
      }
    }).catch(error => {
      alert('System error');
      console.log('There has been a problem with fetching (Saving Questions API): ', error);
    });
  }

  apiGetLocation() {
    var API_URL = config.API_LINK;
    var API_PARAM = config.API_PARAMETERS;
    var JOBID = this.state.chosenJobId;
    var APPID = this.state.apiAppId;
    var formData = new FormData();
    formData.append('jobid', JOBID);
    formData.append('appid', APPID);
    fetch(API_URL + 'api/kiosk/job-app/listloc' + API_PARAM, {
      method: 'POST',
      body: formData
    }).then(response => {
      return response.json();
    }).then(json => {
      console.log("Result Get Locations:", json);

      if (json.code === "00") {
        localStorage.setItem('dataLoc', JSON.stringify(json.data[0]));
        this.setState({
          activeStep: "location"
        });
        this.setState({
          stepQuestions: "hide",
          stepLocation: ""
        });
        this.getLocalLocations();
      } else {
        alert(json.message[0]);
      }
    }).catch(error => {
      alert('System error');
      console.log('There has been a problem with fetching (Get Locations API): ', error);
    });
  }

  apiSaveLocation() {
    var COUNTANS = 0;
    var LOCID = "";
    this.state.selectedLoc.map(key => {
      if (key.check === "yes") {
        if (LOCID === "") {
          COUNTANS++;
          LOCID += key.locid + ",";
        } else {
          COUNTANS++;
          LOCID += key.locid;
        }
      }
    }); //

    if (COUNTANS === 2) {
      var API_URL = config.API_LINK;
      var API_PARAM = config.API_PARAMETERS;
      var JOBID = this.state.chosenJobId;
      var APPID = this.state.apiAppId;
      var formData = new FormData();
      formData.append('jobid', JOBID);
      formData.append('appid', APPID);
      formData.append('locids', APPID);
      fetch(API_URL + 'api/kiosk/job-app/submitloc' + API_PARAM, {
        method: 'POST',
        body: formData
      }).then(response => {
        return response.json();
      }).then(json => {
        console.log("Result Save Locations:", json);

        if (json.code === "00") {
          this.setState({
            activeStep: "thanks_page"
          });
          this.setState({
            stepLocation: "hide",
            stepThanks: ""
          });
          localStorage.removeItem("newData");
        } else {
          alert(json.message[0]);
        }
      }).catch(error => {
        alert('System error');
        console.log('There has been a problem with fetching (Save Locations API): ', error);
      });
    } else {
      alert("Maximum selection allowed is 2");
    }
  }

  nextStep(status) {
    if (status === "apply") {
      this.setState({
        activeStep: "sms_step1"
      });
      this.setState({
        stepApply: "hide",
        stepNumber: ""
      });
      this.setState({
        smsBtnNext: "btn-apply",
        smsBtnVerify: "btn-apply hide",
        smsTxtVerify: "hide"
      });
      this.setState({
        stepApply: "hide",
        stepNumber: ""
      });
    } else if (status === "sms_next") {
      this.apiGetOTP();
    } else if (status === "sms_verify") {
      this.apiVerifyOTP();
    } else if (status === "dtls_next") {
      this.apiSaveUser();
    } else if (status === "questions") {
      setTimeout(() => {
        this.apiSaveQuestions();
      }, 100);
    } else if (status === "location") {
      this.apiSaveLocation();
    } else if (status === "browse") {
      window.location.href = "../home";
    }
  }

