  nextStep(status) {
    if (status === "apply") {
      this.setState({
        activeStep: "sms_step1"
      });
      this.setState({
        stepApply: "hide",
        stepNumber: ""
      });
      this.setState({
        smsBtnNext: "btn-apply",
