      });
      this.setState({
        stepApply: "hide",
        stepNumber: ""
      });
    } else if (status === "sms_next") {
      this.apiGetOTP();
    } else if (status === "sms_verify") {
      this.apiVerifyOTP();
    } else if (status === "dtls_next") {
      this.setState({
        activeStep: "questions"
      });
      this.setState({
        stepInfo: "hide",
        stepQuestions: ""
      });
      this.apiRequestQuestions();
    } else if (status === "questions") {
      this.setState({
        activeStep: "location"
      });
      this.setState({
        stepQuestions: "hide",
        stepLocation: ""
      });
    } else if (status === "location") {
      this.setState({
        activeStep: "thanks_page"
      });
      this.setState({
        stepLocation: "hide",
        stepThanks: ""
      });
    } else if (status === "browse") {
      window.location.href = "home";
    }
  }

