  quesAns(quesid, ansid, nexid, anstxt, imgurl) {
    console.log("Question ID: ", quesid);
    console.log("Answer ID: ", ansid);
    console.log("Next Question ID: ", nexid);
    console.log("Old Answers: ", this.state.apiQues);
    var newAns = [];
    this.state.apiQues.forEach(function (qst) {
      if (qst.quesid === quesid) {
        newAns.push({
          quesid: qst.quesid,
          ansid: ansid,
          anstext: anstxt,
          img: imgurl
        });
      } else {
        newAns.push({
          quesid: qst.quesid,
          ansid: qst.ansid,
          anstext: qst.anstext,
          img: qst.img
        });
      }
    });

    if (nexid === '') {
      console.log('Done');
      this.nextStep('questions');
    } else {
      newAns.push({
        quesid: nexid,
        ansid: "",
        anstext: "",
        img: ""
      });
    }

    console.log('New Answers: ', newAns);
    this.setState({
      apiQues: newAns
    });
  }

