import React, { Component } from 'react';
import { Container, Row, Col, Button, FormControl, Navbar, Card } from 'react-bootstrap';
import imgFP from '../img/logo-fair-price.png';

class Home extends Component {

    state = {
        totaljobs: "",
        apiUrl: "https://feed.fastjobs.ph/jobs/search?skip_key=1",
        apiKeyUrl: "&keyword=",
        keySearch: "",
        tags: ['tag 1', 'tag 2', 'tag 3'],
        counters: [
            { id:1, value:4 },
            { id:2, value:0 },
            { id:3, value:0 },
            { id:4, value:0 }
        ],
        homeTiles:[],
        img1: "", img1_ttl: "",
        img2: "", img2_ttl: "",
        img3: "", img3_ttl: "",
        img4: "", img4_ttl: "",
        img5: "", img5_ttl: "",
        img6: "", img6_ttl: ""
    }

    getLocalData() {
        if (localStorage.getItem('homeTiles')) { //get data from local storage
            var localData = JSON.parse(localStorage.getItem('homeTiles'));
            this.setState({ homeTiles: localData });
            this.setState({ img1: localData.data[0].img });
            this.setState({ img1_ttl: localData.data[0].jobttl });
            this.setState({ img2: localData.data[1].img });
            this.setState({ img2_ttl: localData.data[1].jobttl });
            this.setState({ img3: localData.data[2].img });
            this.setState({ img3_ttl: localData.data[2].jobttl });
            this.setState({ img4: localData.data[3].img });
            this.setState({ img4_ttl: localData.data[3].jobttl });
            this.setState({ img5: localData.data[4].img });
            this.setState({ img5_ttl: localData.data[4].jobttl });
            this.setState({ img6: localData.data[5].img });
            this.setState({ img6_ttl: localData.data[5].jobttl });
            console.log("Using data from local.");
        }
    }

    handleApiRequest() {
        console.log('Mode:',process.env.NODE_ENV);

        this.getLocalData(); //get local data first
        console.log("Rendered API Request");

