  getLocalData() {
    if (localStorage.getItem('newData')) {
      var savData = JSON.parse(localStorage.getItem('newData'));
      var locData = JSON.parse(localStorage.getItem('homeTiles'));

      if (savData.jobid) {
        var savJobId = savData.jobid;
        var locJobs = locData.data;

        if (locJobs.length > 0) {
          for (var i = 0; i < locJobs.length; i++) {
            if (locJobs[i].jobid === savJobId) {
              this.setState({
                chosenJobId: savJobId
              });
              this.setState({
                chosenJob: locJobs[i].jobttl
              });
              this.setState({
                jobBanner: locJobs[i].bannerurl
              });
              this.setState({
                jobDescHtml: locJobs[i].desc
              });
            }
          }
        }
      }
    } //


    if (localStorage.getItem('questions')) {
      var locData = JSON.parse(localStorage.getItem('questions'));
      this.setState({
        apiReturnQst: locData
      });
    }
  }

  getLocalQuestions() {
    var dataLocalQues = this.state.apiReturnQst.data[0];
    var i = 0;
    Object.keys(dataLocalQues).map(key => {
      i++;

      if (i === 1) {
        this.setState({
          questTitle: dataLocalQues[key].ques
        });
      }

      if (i === 2) {
        this.state.apiQues.push({
          quesid: dataLocalQues[key].quesid,
          ansid: "",
          anstext: "",
          img: ""
        });
      }
    });
  }

