  backStep() {
    console.log('Active Step:', this.state.activeStep);

    if (this.state.activeStep === "job_desc") {
      window.location.href = "home";
    } else if (this.state.activeStep === "sms_step1" || this.state.activeStep === "sms_step2") {
      this.setState({
