    var formData = new FormData();
    formData.append('kioskid', this.state.apiKioskId);
    console.log("Kiosk ID: ", this.state.apiKioskId);
    formData.append('jobid', this.state.chosenJobId);
    console.log("Job ID: ", this.state.chosenJobId);
    formData.append('mobn', this.state.apiTxtMobile);
    console.log("Mobile No: ", this.state.apiTxtMobile);
    fetch(API_URL + 'api/kiosk/job-app/sendotp' + API_PARAM, {
      method: 'POST',
      body: formData
    }).then(response => {
      return response.json();
    }).then(json => {
      console.log("Result Questions:", json);

      if (json.code === "00") {
        this.setState({
          apiAppId: json.data[0].appid
        });
