import React, { Component } from 'react';
import { Container, Row, Col, Button, FormControl, Navbar, Card, InputGroup } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import{faAngleLeft, faAngleRight }from'@fortawesome/free-solid-svg-icons';
import imgFairPrice from '../img/fairprice-logo-header.png';
import imgBack from '../img/icon-back.png';
import imgBtnApply from '../img/button-apply.png';
import imgBtnNext from '../img/button-next.png';
import imgBtnVerify from '../img/button-verify.png';
import imgBtnBrowse from '../img/button-browse-jobs.png';
import imgSMS from '../img/icon-sms-otp.png';
import imgInfo from '../img/icon-basic-info.png';
import imgChkOn from '../img/icon-checkbox-on.png';
import imgChkOff from '../img/icon-checkbox-off.png';

class Home extends Component {

    state = {
        apiUrlProd: "https://feed.fastjobs.sg/",
        apiUrlDev: "https://feed-qa.fastjobs.fun/",
        apiParameters: "?c=kioskntuc&key=4173221dfccb31979361d4194f3b2fb7&locale=en&v=1.0",
        apiKioskId: "1060",
        apiAppId: "",
        apiTxtMobile: "",
        apiTxtOTP: "",

        apiReturnQst: [],
        questTitle: "",
        apiQues: [
            //{ "quesid":"1029", "ansid":"", "anstext":"" }
        ],
        quesArr: [],

        counters: [
            { id:1, value:4 },
            { id:2, value:0 },
            { id:3, value:0 },
            { id:4, value:0 }
        ],

        activeStep: "job_desc", homeTiles:[],

        jobBanner: "",
        jobDescHtml: "",
        chosenJob: "",
        chosenJobId: "",

        stepApply: "",
        stepNumber: "hide",
        stepInfo: "hide",
        chkBoxAgree: imgChkOff,
        stepQuestions: "hide",
        mobType: "password", //text or password
        mobMsg: "SHOW", //SHOW or HIDE
        smsBtnNext: "btn-apply hide",
        smsBtnVerify: "btn-apply hide",
        smsTxtVerify: "hide"

    }

    getLocalData() {
        if (localStorage.getItem('newData')) {
            var savData = JSON.parse(localStorage.getItem('newData'));
            var locData = JSON.parse(localStorage.getItem('homeTiles'));
            if (savData.jobid) {
                var savJobId = savData.jobid;
                var locJobs = locData.data;
                if (locJobs.length > 0) {
                    for (var i=0; i < locJobs.length; i++) {
                        if (locJobs[i].jobid === savJobId) {
                            this.setState({ chosenJobId: savJobId });
                            this.setState({ chosenJob: locJobs[i].jobttl });
                            this.setState({ jobBanner: locJobs[i].bannerurl });
                            this.setState({ jobDescHtml: locJobs[i].desc });
                        }
                    }
                }
            }
        }
        //
        if (localStorage.getItem('questions')) {
            var locData = JSON.parse(localStorage.getItem('questions'));
            this.setState({ apiReturnQst: locData });
        }
    }

    getLocalQuestions() {
        var dataLocalQues = this.state.apiReturnQst.data[0];
        var i = 0;
        Object.keys(dataLocalQues).map((key) => {
            i++;
            if (i === 1) {
                this.setState({ questTitle: dataLocalQues[key].ques });
            }
            if (i === 2) {
                this.state.apiQues.push({ quesid: dataLocalQues[key].quesid, ansid: "", anstext: "", img:"" });
            }
         })
    }

    handleApiRequest() {
        console.log('Mode:',process.env.NODE_ENV);
        console.log("Rendered API Request");
    }

    backStep() {
        console.log('Active Step:', this.state.activeStep);
        if (this.state.activeStep === "job_desc") {
            window.location.href = "home";
        } else if (this.state.activeStep === "sms_step1" || this.state.activeStep === "sms_step2") {
            this.setState({ activeStep:"job_desc" });
            this.setState({ stepApply:"", stepNumber:"hide" });
        } else if (this.state.activeStep === "contact_details") {
            this.setState({ activeStep:"sms_step2" });
            this.setState({ stepNumber:"", stepInfo:"hide" });
        } else if (this.state.activeStep === "questions") {
            this.setState({ activeStep:"contact_details" });
            this.setState({ stepInfo:"", stepQuestions:"hide" });
        }
    }

    mobShowHide() {
        if (this.state.mobType === "text") {
            this.setState({ mobType:"password", mobMsg:"SHOW" });
        } else {
            this.setState({ mobType:"text", mobMsg:"HIDE" });
        }
    }

    chkBoxAgree() {
        if (this.state.chkBoxAgree === imgChkOff) {
            this.setState({ chkBoxAgree:imgChkOn });
        } else {
            this.setState({ chkBoxAgree:imgChkOff });
        }
    }

    txtMobileNo = (event) => {
        var input = event.target.value;
        this.setState({ apiTxtMobile:input });
    }

    txtOTP = (event) => {
        var input = event.target.value;
        this.setState({ apiTxtOTP:input });
    }

    apiGetOTP() {
        var API_URL = this.state.apiUrlProd;
        var API_PARAM = this.state.apiParameters;
        //if (process.env.NODE_ENV === "development") { API_URL = this.state.apiUrlDev; }

        var formData = new FormData();
        formData.append('kioskid', this.state.apiKioskId); console.log("Kiosk ID: ",this.state.apiKioskId);
        formData.append('jobid', this.state.chosenJobId); console.log("Job ID: ",this.state.chosenJobId);
        formData.append('mobn', this.state.apiTxtMobile); console.log("Mobile No: ",this.state.apiTxtMobile);
        fetch(API_URL+'api/kiosk/job-app/sendotp'+API_PARAM, {
            method: 'POST',
            body: formData
        }).then(response => {
            return response.json();
        }).then(json => {
            console.log("Result Questions:", json);
            if (json.code === "00") {
                this.setState({ apiAppId:json.data[0].appid });
            } else {
                alert(json.message[0]);
            }
//            this.setState({ activeStep:"sms_step2" });
//            this.setState({ smsBtnNext:"btn-apply hide", smsBtnVerify:"btn-apply", smsTxtVerify:"" });
        }).catch(error => {
            console.log('There has been a problem with fetching (Get OTP API): ',error);
        });

        this.setState({ activeStep:"sms_step2" });
        this.setState({ smsBtnNext:"btn-apply hide", smsBtnVerify:"btn-apply", smsTxtVerify:"" });
    }

    apiVerifyOTP() {
        var API_URL = this.state.apiUrlProd;
        var API_PARAM = this.state.apiParameters;
        //if (process.env.NODE_ENV === "development") { API_URL = this.state.apiUrlDev; }

        var formData = new FormData();
        formData.append('kioskid', this.state.apiKioskId); console.log("Kiosk ID: ",this.state.apiKioskId);
        formData.append('jobid', this.state.chosenJobId); console.log("Job ID: ",this.state.chosenJobId);
        formData.append('appid', this.state.apiAppId); console.log("App Id: ",this.state.apiAppId); console.log("OTP Code: ",this.state.apiTxtOTP);
        fetch(API_URL+'api/kiosk/job-app/ques-list'+API_PARAM, {
            method: 'POST',
            body: formData
        }).then(response => {
            return response.json();
        }).then(json => {
