  apiSaveLocation() {
    var COUNTANS = 0;
    var LOCID = "";
    this.state.selectedLoc.map(key => {
      if (key.check === "yes") {
        if (LOCID === "") {
          COUNTANS++;
          LOCID += key.locid + ",";
        } else {
          COUNTANS++;
          LOCID += key.locid;
        }
      }
    }); //

    if (COUNTANS === 2) {
      this.showMsg('loading', '');
      var API_URL = config.API_SAVELOCATIONS;
      console.log(API_URL);
      var JOBID = this.state.chosenJobId;
      var APPID = this.state.apiAppId;
      var formData = new FormData();
      formData.append('jobid', JOBID);
      formData.append('appid', APPID);
      formData.append('locids', APPID);
      fetch(API_URL, {
        method: 'POST',
        body: formData
      }).then(response => {
        return response.json();
      }).then(json => {
        if (json.code === "00") {
          this.closeMsg();
          this.setState({
            activeStep: "thanks_page"
          });
          this.setState({
            stepLocation: "hide",
            stepThanks: ""
          });
          localStorage.removeItem("newData");
        } else {
          this.showMsg('alert', json.message[0]);
        }
      }).catch(error => {
        this.showMsg('alert', 'System error. Please send an email to customercare@fastjobs.sg');
        console.log('There has been a problem with fetching (Save Locations API): ', error);
      });
    } else {
      this.showMsg('alert', 'Maximum selection allowed is 2');
    }
  }

  nextStep(status) {
    if (status === "apply") {
      this.setState({
        activeStep: "sms_step1"
      });
      this.setState({
        stepApply: "hide",
        stepNumber: ""
      });
      this.setState({
        smsBtnNext: "btn-apply",
        smsBtnVerify: "btn-apply hide",
        smsTxtVerify: "hide"
      });
      this.setState({
        stepApply: "hide",
        stepNumber: ""
      });
    } else if (status === "sms_next") {
      this.apiGetOTP();
    } else if (status === "sms_verify") {
      this.apiVerifyOTP();
    } else if (status === "dtls_next") {
      this.apiSaveUser();
    } else if (status === "questions") {
      setTimeout(() => {
        this.apiSaveQuestions();
      }, 100);
    } else if (status === "location") {
      this.apiSaveLocation();
    } else if (status === "browse") {
      const LOCID = config.getLocationId(this.props) === "" ? "" : "?locid=" + config.getLocationId(this.props);
      window.location.href = "../home" + LOCID;
    }
  }

