  quesAns(quesid, ansid, nexid, anstxt, imgurl) {
    var newAns = [];
    this.state.apiQues.forEach(function (qst) {
      if (qst.quesid === quesid) {
        newAns.push({
          quesid: qst.quesid,
          ansid: ansid,
          anstext: anstxt,
          img: imgurl
        });
      } else {
        newAns.push({
          quesid: qst.quesid,
          ansid: qst.ansid,
          anstext: qst.anstext,
          img: qst.img
        });
      }
    });

    if (nexid === '') {
      this.nextStep('questions');
    } else {
      newAns.push({
        quesid: nexid,
        ansid: "",
        anstext: "",
        img: ""
      });
    }

    this.setState({
      apiQues: newAns
    });
  }

