                            </div>
                       <div style={{ textAlign:"center", marginBottom:"20px" }}>
                           <img src={imgBtnBrowse} alt="Browse Job" className="btn-apply" onClick={() => this.nextStep('browse')} />
                       </div>
                    </div>
                </div>

            </div>
        );
    }
}

export default Jobs;
