import React, { Component } from 'react';
import { Container, Row, Col, Button, FormControl, Navbar, Card } from 'react-bootstrap';
//import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
//import { faUserAlt, faTags, faMapMarkerAlt, faCalendarAlt, faClock} from '@fortawesome/free-solid-svg-icons';
import imgBanner from '../img/home-banner.png';
import img1 from '../img/tile-retail-assistance.png';
import img2 from '../img/tile-cashier.png';
import img3 from '../img/tile-online-fulfilment-assistance.png';
import img4 from '../img/tile-skilled-cutter.png';
import img5 from '../img/tile-storekeeper.png';
import img6 from '../img/tile-retail-supervisor.png';

class Home extends Component {

    state = {
        totaljobs: "",
        apiUrl: "https://feed.fastjobs.ph/jobs/search?skip_key=1",
        apiKeyUrl: "&keyword=",
        keySearch: "",
        tags: ['tag 1', 'tag 2', 'tag 3'],
        counters: [
            { id:1, value:4 },
            { id:2, value:0 },
            { id:3, value:0 },
            { id:4, value:0 }
        ],
        homeTiles:[]
    }

    handleApiRequest() {
        console.log("Rendered API Request");
        var formData = new FormData();
        formData.append('kioskid', '1040');
        fetch('https://feed-qa.fastjobs.fun/api/kiosk/jobs/list?c=kioskntuc&key=4173221dfccb31979361d4194f3b2fb7&locale=en&v=1.0', {
            method: 'POST',
            body: formData
        }).then(response => {
            return response.json();
        }).then(json => {
            console.log("Result:", json);
            if (json.code == "00") {
                this.setState({ homeTiles:json.data });
            }

            console.log("state data",this.state.homeTiles);

            localStorage.setItem('homeTiles', JSON.stringify(json)); //stored api data to local storage
            localStorage.setItem('jobsListDate', Date.now());

        }).catch(error => {
            console.log('There has been a problem with your fetch operation: ',error);
        });


    }

    componentDidMount() {
        this.handleApiRequest();
    }

    render() { 
        return (
            <Container style={{ marginBottom:"50px" }}>
                <Row>
                    <img 
                        src={imgBanner}
                        alt="header banner"
                        style={{ width:"100%" }}
                    />
                </Row>
                <Row>
                    <Col xs={12} md={12}>
                        <div className="home-hdr" >
                            JOB OPPORTUNITIES
                        </div>
                    </Col>
                </Row>


                <Row className="version-web" >
                    <Col className="home-tileweb">
                        <img 
                            src={img1}
                            alt="img1"
                            className="home-tileimg"
                        />
                        <div className="home-subtle" >
                            RETAIL ASSISTANCE
                        </div>
                    </Col>
                    <Col className="home-tileweb">
                        <img 
                            src={img2}
                            alt="img2"
                            className="home-tileimg"
                        />
                        <div className="home-subtle" >
                            CASHIER
                        </div>
                    </Col>
                    <Col className="home-tileweb">
                        <img 
                            src={img3}
                            alt="img3"
                            className="home-tileimg"
                        />
                        <div className="home-subtle" >
                            ONLINE FULFILMENT ASSISTANCE
                        </div>
                    </Col>
                </Row>
                <Row className="version-web" >
                    <Col>
                        <img 
                            src={img4}
                            alt="img4"
                            className="home-tileimg"
                        />
                        <div className="home-subtle" >
                            SKILLED CUTTER
                        </div>
                    </Col>
                    <Col>
                        <img 
                            src={img5}
                            alt="img5"
                            className="home-tileimg"
                        />
                        <div className="home-subtle" >
                            STOREKEEPER
                        </div>
                    </Col>
                    <Col>
                        <img 
                            src={img6}
                            alt="img6"
                            className="home-tileimg"
                        />
                        <div className="home-subtle" >
                            RETAIL SUPERVISOR
                        </div>
                    </Col>
                </Row>


                <Row id="version-mobile" >
                    <Col xs={12} md={12}>
                        <table style={{ width:"100%" }}>
                            <tbody>
                                <tr>
                                    <td className="home-tblcol">
                                        <img 
                                            src={img1}
                                            alt="header banner"
                                            className="home-tileimg"
                                        />
                                        <div className="homem-subtle" >
                                            RETAIL ASSISTANCE
                                        </div>
                                    </td>
                                    <td className="home-tblcol">
                                        <img 
                                            src={img2}
                                            alt="header banner"
                                            className="home-tileimg"
                                        />
                                        <div className="homem-subtle" >
                                            CASHIER
                                        </div>
                                    </td>
                                </tr>
                                <tr>
                                    <td className="home-tblcol">
                                        <img 
                                            src={img3}
                                            alt="header banner"
                                            className="home-tileimg"
                                        />
                                        <div className="homem-subtle" >
                                            ONLINE FULFILMENT ASSISTANCE
                                        </div>
                                    </td>
                                    <td className="home-tblcol">
                                        <img 
                                            src={img4}
                                            alt="header banner"
                                            className="home-tileimg"
                                        />
                                        <div className="homem-subtle" >
                                            SKILLED CUTTER
                                        </div>
                                    </td>
                                </tr>
                                <tr>
                                    <td className="home-tblcol">
                                        <img 
                                            src={img5}
                                            alt="header banner"
                                            className="home-tileimg"
                                        />
                                        <div className="homem-subtle" >
                                            STOREKEEPER
                                        </div>
                                    </td>
                                    <td className="home-tblcol">
                                        <img 
                                            src={img6}
                                            alt="header banner"
                                            className="home-tileimg"
                                        />
                                        <div className="homem-subtle" >
                                            RETAIL SUPERVISOR
                                        </div>
                                    </td>
                                </tr>
                            </tbody>
                        </table>
                    </Col>
                </Row>
            </Container>
        );
    }

}

export default Home;
