                img5:localData[4].img, jobid5:localData[4].jobid, img5_ttl:localData[4].jobttl,
                img6:localData[5].img, jobid6:localData[5].jobid, img6_ttl:localData[5].jobttl
            });
            console.log("Using data from local.");
        }
    }

    handleApiRequest() {
        let API_URL = config.API_HOMETILES;
        let API_KIOSID = config.API_KIOSKID;

        //--- get local data first ---//
        this.getLocalData();

        //--- api request ---//
        var formData = new FormData();
        formData.append('kioskid', API_KIOSID);
        fetch(API_URL, {
            method: 'POST',
            body: formData
        }).then(response => {
            return response.json();
        }).then(json => {
            console.log("Result:", json);
            if (json.code === "00") {
                this.setState({ 
                    homeTiles:json.data, 
                    img1:json.data[0].img, jobid1:json.data[0].jobid, img1_ttl:json.data[0].jobttl,
                    img2:json.data[1].img, jobid2:json.data[1].jobid, img2_ttl:json.data[1].jobttl,
                    img3:json.data[2].img, jobid3:json.data[2].jobid, img3_ttl:json.data[2].jobttl,
                    img4:json.data[3].img, jobid4:json.data[3].jobid, img4_ttl:json.data[3].jobttl,
                    img5:json.data[4].img, jobid5:json.data[4].jobid, img5_ttl:json.data[4].jobttl,
                    img6:json.data[5].img, jobid6:json.data[5].jobid, img6_ttl:json.data[5].jobttl
                });
                localStorage.setItem('homeTiles', JSON.stringify(json.data)); //stored api data to local storage
                //localStorage.setItem('homeTilesDate', Date.now());
            }

            console.table(this.state.homeTiles);
        }).catch(error => {
            console.log('There has been a problem with fetching (Job List API): ',error);
        });
    }

