import React, { Component } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import imgBanner from '../img/banner-home.jpg';
import * as config from '../config.js';

class Home extends Component {

    state = {
        homeTiles:[],
        tiles:[],
        tiles_mobile:[],
        img1: "", img1_ttl: "", jobid1: "",
        img2: "", img2_ttl: "", jobid2: "",
        img3: "", img3_ttl: "", jobid3: "",
        img4: "", img4_ttl: "", jobid4: "",
        img5: "", img5_ttl: "", jobid5: "",
        img6: "", img6_ttl: "", jobid6: "",
    }

    getLocalData() {
        if (localStorage.getItem('homeTiles')) { //get data from local storage
            var localData = JSON.parse(localStorage.getItem('homeTiles'));
            this.setState({
                homeTiles:localData,
                img1:localData[0].img, jobid1:localData[0].jobid, img1_ttl:localData[0].jobttl,
                img2:localData[1].img, jobid2:localData[1].jobid, img2_ttl:localData[1].jobttl,
                img3:localData[2].img, jobid3:localData[2].jobid, img3_ttl:localData[2].jobttl,
                img4:localData[3].img, jobid4:localData[3].jobid, img4_ttl:localData[3].jobttl,
                img5:localData[4].img, jobid5:localData[4].jobid, img5_ttl:localData[4].jobttl,
                img6:localData[5].img, jobid6:localData[5].jobid, img6_ttl:localData[5].jobttl
            });
            console.log("Using data from local.");
        }
    }

    dataTiles(arr) {
        const ORIGDATA = arr.data;
        const COUNT_ORIG = ORIGDATA.length;
        //const COUNT_ORIG = 5;

        var MAINSET = [];
        var MAIN_COUNT = 0;
        if (COUNT_ORIG > 0) {
            MAIN_COUNT++;
            var NEWSET = [];
            var COUNT = 0;
            ORIGDATA.forEach(function(key) {
                COUNT++;

                //if (COUNT <= 5) {
                    NEWSET.push({ id:COUNT, jobid:key.jobid, ttl:key.jobttl, img:key.img });

                    const MODULUS_CON = COUNT % 3;
                    if (COUNT_ORIG === COUNT) {
                        MAINSET.push({ main_count:MAIN_COUNT, data:NEWSET });

                        if (COUNT_ORIG > 3) {
                            const MODULUS_MISSING = COUNT % 3;
                            if (MODULUS_MISSING === 1) {
                                // plus 2
                                COUNT++;
                                NEWSET.push({ id:COUNT, jobid:"", ttl:"", img:"" });
                                COUNT++;
                                NEWSET.push({ id:COUNT, jobid:"", ttl:"", img:"" });
                            } else if (MODULUS_MISSING === 2) {
                                // plus 1
                                COUNT++;
                                NEWSET.push({ id:COUNT, jobid:"", ttl:"", img:"" });
                            }
                        } 
                    } else if (MODULUS_CON === 0) {
                        MAINSET.push({ main_count:MAIN_COUNT, data:NEWSET });
                        NEWSET = [];
                        MAIN_COUNT++;
                    }
                //}

            });
        }
        this.setState({ tiles:MAINSET });
    }

    dataTilesMobile(arr) {
        const ORIGDATA = arr.data;
        const COUNT_ORIG = ORIGDATA.length;
        const MODULUS_CON = COUNT_ORIG % 2;
        console.log("asd asd",MODULUS_CON);
        //const COUNT_ORIG = 5;

//        var MAINSET = [];
//        var MAIN_COUNT = 0;
//        if (COUNT_ORIG > 0) {
//            MAIN_COUNT++;
//            var NEWSET = [];
//            var COUNT = 0;
//            ORIGDATA.forEach(function(key) {
//                COUNT++;
//                
//                //if (COUNT <= 5) {
//                    NEWSET.push({ id:COUNT, jobid:key.jobid, ttl:key.jobttl, img:key.img });
//
//                    const MODULUS_CON = COUNT % 2;
//                    if (COUNT_ORIG === COUNT) {
//                        MAINSET.push({ main_count:MAIN_COUNT, data:NEWSET });
//                        
//                        if (COUNT_ORIG > 3) {
//                            const MODULUS_MISSING = COUNT % 3;
//                            if (MODULUS_MISSING === 1) {
//                                // plus 2
//                                COUNT++;
//                                NEWSET.push({ id:COUNT, jobid:"", ttl:"", img:"" });
//                                COUNT++;
//                                NEWSET.push({ id:COUNT, jobid:"", ttl:"", img:"" });
//                            } else if (MODULUS_MISSING === 2) {
//                                // plus 1
//                                COUNT++;
//                                NEWSET.push({ id:COUNT, jobid:"", ttl:"", img:"" });
//                            }
//                        } 
//                    } else if (MODULUS_CON === 0) {
//                        MAINSET.push({ main_count:MAIN_COUNT, data:NEWSET });
//                        NEWSET = [];
//                        MAIN_COUNT++;
//                    }
//                //}
//                       
//            });
//        }
//        this.setState({ tiles_mobile:MAINSET });
    }

    handleApiRequest() {
        let API_URL = config.API_HOMETILES;
        let API_KIOSID = config.API_KIOSKID;

        //--- get local data first ---//
        this.getLocalData();

        //--- api request ---//
        var formData = new FormData();
        formData.append('kioskid', API_KIOSID);
        fetch(API_URL, {
            method: 'POST',
            body: formData
        }).then(response => {
            return response.json();
        }).then(json => {
            console.log("Result:", json);
            if (json.code === "00") {
                this.dataTiles(json);
                this.dataTilesMobile(json);
                this.setState({ 
                    homeTiles:json.data, 
                    img1:json.data[0].img, jobid1:json.data[0].jobid, img1_ttl:json.data[0].jobttl,
                    img2:json.data[1].img, jobid2:json.data[1].jobid, img2_ttl:json.data[1].jobttl,
                    img3:json.data[2].img, jobid3:json.data[2].jobid, img3_ttl:json.data[2].jobttl,
                    img4:json.data[3].img, jobid4:json.data[3].jobid, img4_ttl:json.data[3].jobttl,
                    img5:json.data[4].img, jobid5:json.data[4].jobid, img5_ttl:json.data[4].jobttl,
                    img6:json.data[5].img, jobid6:json.data[5].jobid, img6_ttl:json.data[5].jobttl
                });
                localStorage.setItem('homeTiles', JSON.stringify(json.data)); //stored api data to local storage
                //localStorage.setItem('homeTilesDate', Date.now());
            }

            console.table(this.state.homeTiles);
        }).catch(error => {
            console.log('There has been a problem with fetching (Job List API): ',error);
        });
    }

    componentDidMount() {
        this.handleApiRequest();
        config.getLocationId(this.props);
    }

    nextStep(jobId) {
        const LOCID = config.getLocationId(this.props) === "" ? "" : "&locid="+config.getLocationId(this.props);
        console.log("Job ID:", jobId);
        localStorage.setItem('newData', JSON.stringify({ jobid:jobId }));
        window.location.href = "job/online-picker?id="+jobId+LOCID;
    }

    render() { 
        return (
            <div className="contnr" >
            <Container style={{ marginBottom:"20px" }}>
                <Row>
                    <img 
                        src={imgBanner}
                        alt="header banner"
                        style={{ width:"100%" }}
                    />
                </Row>
                <Row>
                    <Col xs={12} md={12}>
                        <div className="home-hdr" >
                            JOB OPPORTUNITIES
                        </div>
                    </Col>
                </Row>

                    {
                        this.state.tiles.map( keys => (
                            <Row key={keys.main_count} className="version-web" >
                                {
                                    keys.data.map( key => (
                                        <Col key={key.jobid} className="home-tileweb">
                                            <img 
                                                src={key.img}
                                                alt={key.ttl}
                                                className={ key.ttl === '' ? 'home-tileimg hide' : 'home-tileimg' }
                                                onClick={() => this.nextStep(key.jobid)}
                                            />
                                            <div 
                                                className={ key.ttl === '' ? 'home-subtle hide' : 'home-subtle' } 
                                                onClick={() => this.nextStep(key.jobid)}
                                            >
                                                {key.ttl}
                                            </div>
                                        </Col>
                                    ))
                                }
                            </Row>      
                        ))
                    }

                <Row id="version-mobile" >
                    <Col xs={12} md={12}>
                        <table style={{ width:"100%" }}>
                            <tbody>
                                <tr>
                                    <td className="home-tblcol">
                                        <img 
                                            src={this.state.img1}
