import React, { Component } from 'react';
import { Container, Row, Col, Button, FormControl, Navbar, Card } from 'react-bootstrap';
import imgBanner from '../img/banner-home.jpg';

class Home extends Component {

    state = {
        totaljobs: "",
        apiUrl: "https://feed.fastjobs.ph/jobs/search?skip_key=1",
        apiKeyUrl: "&keyword=",
        keySearch: "",
        tags: ['tag 1', 'tag 2', 'tag 3'],
        counters: [
            { id:1, value:4 },
            { id:2, value:0 },
            { id:3, value:0 },
            { id:4, value:0 }
        ],
        homeTiles:[],
        img1: "", img1_ttl: "", jobid1: "",
        img2: "", img2_ttl: "", jobid2: "",
        img3: "", img3_ttl: "", jobid3: "",
        img4: "", img4_ttl: "", jobid4: "",
        img5: "", img5_ttl: "", jobid5: "",
        img6: "", img6_ttl: "", jobid6: ""
    }

    getLocalData() {
        if (localStorage.getItem('homeTiles')) { //get data from local storage
            var localData = JSON.parse(localStorage.getItem('homeTiles'));
            this.setState({
                homeTiles: localData,
                img1: localData.data[0].img, 
                jobid1: localData.data[0].jobid,
                img1_ttl: localData.data[0].jobttl,
                img2: localData.data[1].img, 
                jobid2: localData.data[1].jobid,
                img2_ttl: localData.data[1].jobttl,
                img3: localData.data[2].img, 
                jobid3: localData.data[2].jobid,
                img3_ttl: localData.data[2].jobttl,
                img4: localData.data[3].img, 
                jobid4: localData.data[3].jobid,
                img4_ttl: localData.data[3].jobttl,
                img5: localData.data[4].img, 
                jobid5: localData.data[4].jobid,
                img5_ttl: localData.data[4].jobttl,
                img6: localData.data[5].img, 
                jobid6: localData.data[5].jobid,
                img6_ttl: localData.data[5].jobttl
            });
            console.log("Using data from local.");
        }
    }

    handleApiRequest() {
        console.log('Mode:',process.env.NODE_ENV);

        this.getLocalData(); //get local data first
        console.log("Rendered API Request");

        var formData = new FormData();
        formData.append('kioskid', '1060');
        fetch('https://feed.fastjobs.sg/api/kiosk/jobs/list?c=kioskntuc&key=4173221dfccb31979361d4194f3b2fb7&locale=en&v=1.0', {
            method: 'POST',
            body: formData
        }).then(response => {
            return response.json();
        }).then(json => {
            console.log("Result:", json);
            if (json.code === "00") {
                this.setState({ 
                    homeTiles: json.data,
                    img1: json.data[0].img, 
                    jobid1: json.data[0].jobid,
                    img1_ttl: json.data[0].jobttl,
                    img2: json.data[1].img, 
                    jobid2: json.data[1].jobid,
                    img2_ttl: json.data[1].jobttl,
                    img3: json.data[2].img, 
                    jobid3: json.data[2].jobid,
                    img3_ttl: json.data[2].jobttl,
                    img4: json.data[3].img, 
                    jobid4: json.data[3].jobid,
                    img4_ttl: json.data[3].jobttl,
                    img5: json.data[4].img, 
                    jobid5: json.data[4].jobid,
                    img5_ttl: json.data[4].jobttl,
                    img6: json.data[5].img, 
                    jobid6: json.data[5].jobid,
                    img6_ttl: json.data[5].jobttl
                });
                localStorage.setItem('homeTiles', JSON.stringify(json)); //stored api data to local storage
                localStorage.setItem('homeTilesDate', Date.now());
            }
            console.log("Online state data:",this.state.homeTiles);
        }).catch(error => {
            console.log('There has been a problem with your fetch operation: ',error);
        });
    }

    componentDidMount() {
        this.handleApiRequest();
    }

    nextStep(jobId) {
        console.log("Job ID:", jobId);
        localStorage.setItem('newData', JSON.stringify({ jobid:jobId }));
        window.location.href = "job";
    }

    render() { 
        return (
            <div className="" >
            <Container style={{ marginBottom:"50px" }}>
                <Row>
                    <img 
                        src={imgBanner}
                        alt="header banner"
                        style={{ width:"100%" }}
                    />
                </Row>
                <Row>
                    <Col xs={12} md={12}>
                        <div className="home-hdr" >
                            JOB OPPORTUNITIES
                        </div>
                    </Col>
                </Row>


                <Row className="version-web" >
                    <Col className="home-tileweb">
                        <img 
                            src={this.state.img1}
                            alt="img1"
                            className="home-tileimg"
                            onClick={() => this.nextStep(this.state.jobid1)}
                        />
                        <div 
                            className="home-subtle" 
                            onClick={() => this.nextStep(this.state.jobid1)}
                        >
                            {this.state.img1_ttl}
                        </div>
                    </Col>
                    <Col className="home-tileweb">
                        <img 
                            src={this.state.img2}
                            alt="img2"
                            className="home-tileimg"
                            onClick={() => this.nextStep(this.state.jobid2)}
                        />
                        <div 
                            className="home-subtle"
                            onClick={() => this.nextStep(this.state.jobid2)}
                        >
                            {this.state.img2_ttl}
                        </div>
                    </Col>
                    <Col className="home-tileweb">
                        <img 
                            src={this.state.img3}
                            alt="img3"
                            className="home-tileimg"
                            onClick={() => this.nextStep(this.state.jobid3)}
                        />
                        <div 
                            className="home-subtle" 
                            onClick={() => this.nextStep(this.state.jobid3)} 
                        >
                            {this.state.img3_ttl}
                        </div>
                    </Col>
                </Row>
                <Row className="version-web" >
                    <Col>
                        <img 
                            src={this.state.img4}
                            alt="img4"
                            className="home-tileimg"
                            onClick={() => this.nextStep(this.state.jobid4)}
                        />
                        <div 
                            className="home-subtle" 
                            onClick={() => this.nextStep(this.state.jobid4)}
                        >
                            {this.state.img4_ttl}
                        </div>
                    </Col>
                    <Col>
                        <img 
                            src={this.state.img5}
                            alt="img5"
                            className="home-tileimg"
                            onClick={() => this.nextStep(this.state.jobid5)}
                        />
                        <div 
                            className="home-subtle" 
                            onClick={() => this.nextStep(this.state.jobid5)}
                        >
                            {this.state.img5_ttl}
                        </div>
                    </Col>
                    <Col>
                        <img 
                            src={this.state.img6}
                            alt="img6"
                            className="home-tileimg"
                            onClick={() => this.nextStep(this.state.jobid6)}
                        />
                        <div 
                            className="home-subtle" 
                            onClick={() => this.nextStep(this.state.jobid6)}
                        >
                            {this.state.img6_ttl}
                        </div>
                    </Col>
                </Row>


                <Row id="version-mobile" >
                    <Col xs={12} md={12}>
                        <table style={{ width:"100%" }}>
                            <tbody>
                                <tr>
                                    <td className="home-tblcol">
                                        <img 
                                            src={this.state.img1}
                                            alt="header banner"
                                            className="home-tileimg"
                                            onClick={() => this.nextStep(this.state.jobid1)}
                                        />
                                        <div className="homem-subtle" onClick={() => this.nextStep(this.state.jobid1)} >
                                            {this.state.img1_ttl}
                                        </div>
                                    </td>
                                    <td className="home-tblcol">
                                        <img 
                                            src={this.state.img2}
                                            alt="header banner"
                                            className="home-tileimg"
