        } else if (MOBNUMLEN === 10) { 
            let begin_char = MOBNUM.substring(0,2);
            let fir_char = MOBNUM.charAt(2);
            if (begin_char === "65") {
                if (fir_char ==="8" || fir_char ==="9") {
