            } else { error++;}
        } else if (MOBNUMLEN === 11) { 
            let begin_char = MOBNUM.substring(0,3);
            let fir_char = MOBNUM.charAt(3);
            if (begin_char === "+65") {
                if (fir_char ==="8" || fir_char ==="9") {
                    //
                } else { error++; }
            } else { error++; }
        } else { error++; }
    }
    return error;
}

