import React, { Component } from 'react';
import { Container, Row, Col, Button, FormControl, Navbar, Card } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faAngleLeft, faAngleRight } from '@fortawesome/free-solid-svg-icons';
import imgFairPrice from '../img/FairPrice-Logo.png';
import imgSMS from '../img/SMS.png';
import imgDetails from '../img/Contact-Details.png';
import imgSuccess from '../img/Success-Applied.png';

class Home extends Component {

    state = {
        totaljobs: "",
        apiUrl: "https://feed.fastjobs.ph/jobs/search?skip_key=1",
        apiKeyUrl: "&keyword=",
        keySearch: "",
        tags: ['tag 1', 'tag 2', 'tag 3'],
        counters: [
            { id:1, value:4 },
            { id:2, value:0 },
            { id:3, value:0 },
            { id:4, value:0 }
        ],
        homeTiles:[],
        jobBanner: "",
        jobDescHtml: '<p style="text-align: center; font-size: 35px; margin: 0; padding: 0;"><strong>Job Responsibilities</strong></p><p style="margin: 0; padding: 0;"><img style="display: block; margin-left: auto; margin-right: auto;" src="https://www.fastjobs.sg/img/kiosk/jobs/desc/cashier.jpg?t=1552030242" alt="" width="100%" /></p><p style="text-align: center; font-size: 35px; padding: 0;"><strong>Working Hours</strong> </p><ul style="font-size: 26px;width:80%;margin:0 auto; padding-left: 15%;"><li>6 days work week (including Weekends & Public Holidays)</li><li>Rotating shift<ul style="list-style: none; margin:0 auto; padding-left: 5%;"><li>- Full-Time: 7am–3pm / 3pm–11pm</li><li>- Part-Time: 7am–12pm / 12pm–5pm / 5pm–10pm</li></ul></li><li>Permanent night shift (11pm–7am) position available</li></ul>',
        stepApply: "",
        stepNumber: "hide"
    }

    getData() {
        if (localStorage.getItem('newData')) {
            var savData = JSON.parse(localStorage.getItem('newData'));
            var locData = JSON.parse(localStorage.getItem('homeTiles'));
            if (savData.jobid) {
                var savJobId = savData.jobid;
                var locJobs = locData.data;
                if (locJobs.length > 0) {
                    for (var i = 0; i < locJobs.length; i++) {
                        if (locJobs[i].jobid === savJobId) {
                            this.setState({ jobBanner: locJobs[i].bannerurl });
                            this.setState({ jobDescHtml: locJobs[i].desc });
                        }
                    }

                }
            }

        }
    }

    handleApiRequest() {
        console.log('Mode:',process.env.NODE_ENV);
        console.log("Rendered API Request");
    }

    backStep() {
        window.location.href = "home";
    }

    nextStep(status) {
        if (status === "apply") {
            this.setState({ stepApply:"hide", stepNumber:"" });
        }
        console.log(status);
    }

    componentDidMount() {
        this.getData();
        this.handleApiRequest();
    }

    render() { 
        return (
            <div>
                <Container className="fixed-top">
                    <Row>
                        <Col xs={12} md={12}>
                            <div style={{ backgroundColor:"#1c5abc", textAlign:"center", padding:"20px" }} >
                                <table style={{ width:"100%" }}>
                                    <tbody>
                                        <tr>
                                            <td align={"center"} style={{ width:"5%"}} >
                                                 <span className="btnback" onClick={() => this.backStep()} >
                                                    <FontAwesomeIcon icon={faAngleLeft} />
                                                 </span>
                                            </td>
                                            <td align={"center"} style={{ width:"90%"}} >
                                                <img 
                                                    src={ imgFairPrice }
                                                    alt="img1"
                                                    className="jobHeader"
                                                />
                                            </td>
                                            <td align={"center"} style={{ width:"5%"}} >

                                            </td>
                                        </tr>
                                    </tbody>
                                </table>
                            </div>
                        </Col>
                    </Row>
                </Container>
                <Container>
                    <Row>
                        <Col xs={12} md={12}>
                            <div style={{ backgroundColor:"#1c5abc", textAlign:"center", padding:"20px" }} >
                                <table style={{ width:"100%" }}>
                                    <tbody>
                                        <tr>
                                            <td align={"center"} style={{ width:"5%"}} >
                                                 <span className="btnback" onClick={() => this.backStep()}>
                                                    <FontAwesomeIcon icon={faAngleLeft} />
                                                 </span>
                                            </td>
                                            <td align={"center"} style={{ width:"90%"}} >
                                                <img 
                                                    src={ imgFairPrice }
                                                    alt="img1"
                                                    className="jobHeader"
                                                />
                                            </td>
                                            <td align={"center"} style={{ width:"5%"}} >

                                            </td>
                                        </tr>
                                    </tbody>
                                </table>
                            </div>
                        </Col>
                    </Row>
                </Container>



                <Container className={this.state.stepApply} style={{ marginBottom:"50px" }} >
                    <Row>
                        <Col xs={12} md={12}>
                            <div id="jobBanner" >
                                <img 
                                    src={ this.state.jobBanner }
                                    alt="img1"
                                    style={{ width:"100%" }}
                                />
                            </div>
                            <div id="jobDesc" dangerouslySetInnerHTML={{__html: this.state.jobDescHtml}} ></div>
                        </Col>
                    </Row>
                </Container>
                <Container className={this.state.stepApply} >
                    <Row>
                        <Col xs={12} md={12}>
                            <div style={{ textAlign:"center", marginBottom:"50px" }}>
                                <span className="btn-apply"  onClick={() => this.nextStep('apply')}  >
                                     Apply Now
                                </span>
                            </div>

                        </Col>
                    </Row>
                </Container>



                <Container className={this.state.stepNumber} style={{ marginBottom:"50px" }}>
                    <Row>
                        <Col xs={12} md={12}>
                            <div style={{ backgroundColor:"#1c5abc", textAlign:"center", padding:"30px 60px 60px 60px" }}>
                                <img src={ imgSMS } alt="SMS" className="img-sms" />
                                <div className="txt-ttl" style={{ color:"white" }} >
                                    <strong>Verification</strong>
                                </div>
                                <div className="txt-subttl" style={{ color:"white" }} >
                                    Please enter your mobile number and we will send you the verification.
                                </div>
                            </div>
                        </Col>
                    </Row>
                </Container>
                <Container className={this.state.stepNumber} >
                    <Row>
                        <Col xs={12} md={12}>
                            <div style={{ textAlign:"center", marginBottom:"50px" }}>
                                <span className="btn-apply" >
                                     Apply Now
                                </span>
                            </div>

                        </Col>
                    </Row>
                </Container>



            </div>
        );
    }

}

export default Home;
